SELECT CompanyName, t1.OrderID, ((1-Discount) * UnitPrice)*Quantity AS TotalPrice FROM Customers INNER JOIN (SELECT [Order Details].OrderID, [Order Details].UnitPrice, [Order Details].Quantity, [Order Details].Discount, [Orders].CustomerID FROM [Order Details] INNER JOIN Orders ON Orders.OrderID=[Order Details].OrderID) AS t1 ON Customers.CustomerID=t1.CustomerID WHERE ((1-Discount) * UnitPrice)*Quantity > 10000